import { useEffect, useState } from 'react';
import Modal from 'react-modal';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

//Modal.setAppElement('#yourAppElement');

//불러오는측에서
// const [modalIsOpen, setIsOpen] = useState(false);
function CommonModal({ modalIsOpen, setIsOpen }) {
  const [inputValue, setInputValue] = useState('');
  const [isDeleteBtnVisible, setDeleteBtnVisible] = useState(false);

  let subtitle;
  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }
  const handleInputChange = (e) => {
    setInputValue(e.targer.value);
  };
  const verifyPoint = 'AGREE TO DELETE';

  const handleVerify = () => {
    if (inputValue === verifyPoint) {
      closeModal();
      setDeleteBtnVisible(false);
    }
  };

  function closeModal() {
    setIsOpen(false);
  }
  return (
    <>
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Delete verify modal"
      >
        <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Hello</h2>
        <div>Input &quot;{verifyPoint}&quot;</div>
        <form>
          <input onChange={(e) => handleInputChange(e)} />
        </form>
        {isDeleteBtnVisible ? (
          <button onClick={() => handleVerify()}>Delete</button>
        ) : (
          ''
        )}
        <button onClick={closeModal}>Close</button>
      </Modal>
    </>
  );
}

export default CommonModal;
