import Editor from './Editors';
import styled from 'styled-components';
import { useState } from 'react';
import axios from 'axios';
import { pushDefaultWithToken } from '../util/axiosHelper';

/* 
  "comments": [
    {
      "id": 1,
      "createdAt": "2022-12-28T17:35:22.259912",
      "likes": [],
      "content": " 댓글 내용입니다 ",
      "likeCount": 0
    },
*/

/**
 * Created by @ldk199662
 * Modified by @KimTank
 * @returns <Comment>
 */
function CommentEditModule({ cont, handleCont}) {
  return (
    <CommentBody>
        <Editor value={cont} setValue={handleCont} />
    </CommentBody>
  );
}

export default CommentEditModule;

const CommentBody = styled.div`
  background-color: #ffffff;
  margin-left: 20px;
`;

const Buttons = styled.div`
  display: flex;
`;

const PostButton = styled.button`
  background-color: hsl(206deg 100% 52%);
  border-radius: 3px;
  border: none;
  color: #ffffff;
  padding: 13px;
  margin-top: 30px;
  margin-bottom: 15px;
`;
