import axios from 'axios';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { RowDiv } from '../styles/StyledStore';
import { IS_ALIVE } from '../util/tokenHelper';
import QuestionRow from './QuestionRow';

const PaginationContainer = styled(RowDiv)`
  margin: 4px 8px;
  width: 100%;
`;

const PaginationButton = styled.button`
  font-size: 15px;
  border: 1px solid grey;
  background-color: transparent;
  width: 50px;
  height: 50px;
  margin: 4px;
`;

/**
 * - Created by @ldk199662
 * - Modified by @KimTank 221231
 * @returns <Main>
 */
function Main() {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  const handleAskBtn = () => {
    navigate('/questionCreate');
  };
  useEffect(() => {
    axios
      .get('/board/posts', {
        withCredentials: true,
        params: {
          page: 1,
          size: 10,
        },
      })
      .then((response) => {
        const { data } = response;
        console.log(data.data);
        setPosts(data.data);
      })
      .catch((error) => alert(error));
  }, []);

  return (
    <MainBody>
      <MainTitle>
        <h2>Top Questions</h2>
        {IS_ALIVE() ? (
          <button onClick={() => handleAskBtn()}>Ask Question</button>
        ) : (
          ''
        )}
      </MainTitle>
      <QuestionRow posts={posts} />
      <PaginationContainer>
        <PaginationButton>1</PaginationButton>
        <PaginationButton>2</PaginationButton>
        <PaginationButton>3</PaginationButton>
        <PaginationButton>4</PaginationButton>
        <PaginationButton>5</PaginationButton>
      </PaginationContainer>
    </MainBody>
  );
}

export default Main;

const MainBody = styled.div`
  background-color: #ffffff;
  margin-top: -30px;
  border-left: 1px solid hsl(210deg 8% 90%);
  /* 변경ty margin-left: 250px; */
`;

const MainTitle = styled.div`
  padding: 30px;
  border-bottom: 1px solid hsl(210deg 8% 90%);
  > h2 {
    font-weight: 500;
  }
  > button {
    background-color: hsl(206deg 100% 52%);
    border-radius: 3px;
    border: none;
    color: #ffffff;
    margin-left: 600px;
    margin-top: -60px;
    padding: 10px;
  }
  Button:hover {
    background-color: #0c63a9;
  }
`;
