import styled from 'styled-components';
import Editor from './Editors';
import StyledInput from './StyledInput';
import { Tag } from './Tag';
import { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { pushDefaultWithToken } from '../util/axiosHelper';

/*
  "title": "질문글 제목입니다 ",
  "content": " 질문글 내용입니다 ",
  "createdAt": "2022-12-28T17:35:16.59537"
*/

/**
 * Created by @ldk199662
 * Modified by @KimTank
 * @returns <AskCreate>
 */
function AskEdit() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [tags, setTags] = useState([]);
  const [post, setPost] = useState({});

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    setPost(location.state.post);
    console.log(post);
    console.log('========================');
    setTitle(post.title);
    setContent(post.content);
    setTags(post.tags);
  }, []);

  const onChangeTitle = (e) => {
    setTitle(e.target.value);
  };

  const onChangeContent = (content) => {
    setContent(content);
  };
  const onChangeTag = (tag) => {
    setTags(tag);
  };

  const onClickDiscard = () => {
    navigate(-1);
  };

  const onClickSubmit = async () => {
    if (title.length < 10) {
      alert('제목을 최소 10글자 이상 적어주세요');
      return false;
    }

    if (content.length < 20) {
      alert('내용을 최소 20글자 이상 적어주세요');
      return false;
    }

    axios
      .patch(
        `${process.env.REACT_APP_EP_POSTS_EDIT}/${post.id}`,
        {
          title: title,
          content: content,
          tag: tags.toString(),
        },
        pushDefaultWithToken()
      )
      .then(() => {
        navigate(-1);
      })
      .catch((error) => {
        console.log(error);
        let errorText;
        const { message } = error;
        const code = Number(message.slice(-3));
        switch (code) {
          case 401:
          case 404:
          case 500:
          default:
            errorText = message;
        }
        return alert(errorText);
      });
  };

  return (
    <AskBody>
      <AskTitleH1>Edit Question</AskTitleH1>
      <AskTitle>
        <AskTitleH2>Writing a good question</AskTitleH2>
        <br />
        <p>
          You’re ready to{' '}
          <a href="https://stackoverflow.com/help/how-to-ask">ask</a> a{' '}
          <a href="https://stackoverflow.com/help/on-topic">
            programming-related question
          </a>{' '}
          and this form will help guide you through the process.
        </p>
        <p>
          Looking to ask a non-programming question? See{' '}
          <a href="https://stackexchange.com/sites#technology">
            the topics here
          </a>{' '}
          to find a relevant site.
        </p>
        <br />
        <h5>Steps</h5>
        <br />
        <UlContent>
          <li>Summarize your problem in a one-line title.</li>
          <li>Describe your problem in more detail.</li>
          <li>Describe what you tried and what you expected to happen.</li>
          <li>
            Add “tags” which help surface your question to members of the
            community.
          </li>
          <li>Review your question and post it to the site.</li>
        </UlContent>
      </AskTitle>
      <TitleContent>
        <h5>Title</h5>
        <p>
          Be specific and imagine you’re asking a question to another person.
        </p>
        <div className="input-title">
          <StyledInput
            id="title"
            name="title"
            type="text"
            placeholder="e.g. Is there an R function for finding the index of an element in a vector?"
            onChange={onChangeTitle}
            value={title}
          />
        </div>
      </TitleContent>
      <AskContent>
        <h5>What are the details of your problem?</h5>
        <p>
          Introduce the problem and expand on what you put in the title. Minimum
          20 characters.
        </p>

        <Editor value={content} setValue={onChangeContent} />
      </AskContent>
      {/* <AskExpect className="ask-expect">
        <h5>What did you try and what were you expecting?</h5>
        <p>
          Describe what you tried, what you expected to happen, and what
          actually resulted. Minimum 20 characters.
        </p>
        <Editor />
      </AskExpect> */}
      <AskTags className="ask-tags">
        <h5>Tags</h5>
        <p>
          Add up to 5 tags to describe what your question is about. Start typing
          to see suggestions.
        </p>
        <Tag tags={tags} setTags={setTags} onChange={onChangeTag} />
      </AskTags>
      <Buttons>
        <SubmitButton
          className="submit-button"
          type="button"
          onClick={onClickSubmit}
        >
          Post your question
        </SubmitButton>

        <DiscardButton
          className="dicard-button"
          type="button"
          onClick={onClickDiscard}
        >
          Discard draft
        </DiscardButton>
      </Buttons>
    </AskBody>
  );
}
export default AskEdit;

const AskBody = styled.div`
  background-color: #f1f2f3;
  margin-left: 250px;
`;
const AskTitle = styled.div`
  padding: 30px;
  margin: 30px;
  border: 1px solid hsl(206deg 90% 70%);
  background-color: hsl(206deg 100% 97%);
  border-radius: 3px 3px 3px 3px;
`;
const AskTitleH1 = styled.h1`
  font-size: 27px;
  margin-left: 27px;
  margin-bottom: 55px;
`;
const AskTitleH2 = styled.h2`
  font-size: 19.8px;
  font-weight: 400;
`;
const UlContent = styled.ul`
  list-style-type: disc;
  font-size: 13px;
  font-weight: 400;
  margin-left: 28px;
`;
const TitleContent = styled.div`
  padding: 30px;
  margin: 30px;
  border: 1px solid hsl(210deg 8% 90%);
  background-color: #ffffff;
  margin-top: -10px;
  border-radius: 3px 3px 3px 3px;
  > h5 {
    font-size: 15px;
  }
  > p {
    font-size: 12px;
    margin-bottom: 3px;
  }
`;
const AskContent = styled.div`
  padding: 30px;
  margin: 30px;
  border: 1px solid hsl(210deg 8% 90%);
  background-color: #ffffff;
  margin-top: -17px;
  position: relative;
  border-radius: 3px 3px 3px 3px;
  > h5 {
    font-size: 15px;
  }
  > p {
    font-size: 12px;
    margin-bottom: 3px;
  }
  > textarea {
    overflow: auto;
    display: flex;
    flex-direction: column;
    padding: 30px;
    margin: 30px;
  }
`;
