import axios from 'axios';
import { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import CommonButton, {
  BUTTON_TYPE_USER_DELETE,
  BUTTON_TYPE_USER_EDIT,
} from '../components/CommonButton';
import Sidebar from '../components/Sidebar';
import { MainContainer } from '../styles/StyledStore';

const Comments = ({ comments }) => {};

/**
 * made by @ldk199662
 * since 22-12-30
 * @returns
 */
function QuestionDetail() {
  const [post, setPost] = useState({});
  const { state } = useLocation();

  useEffect(() => {
    axios
      .get('/board/posts/' + state.post.id, {
        withCredentials: true,
      })
      .then((response) => {
        const { data } = response;
        setPost(data);
        console.log(`========================`);
        console.log(data.tags);
      })
      .catch((error) => alert(error));
  }, []);
  return (
    <MainContainer>
      <div>
        <div>
          <div id="buttons">
            <CommonButton buttonType={BUTTON_TYPE_USER_EDIT} cont="Edit post" />
            <CommonButton
              buttonType={BUTTON_TYPE_USER_DELETE}
              cont="Delete post"
            />
          </div>
          <div id="etcs">
            <div>{post.createdAt}</div>
            <div>LIKES: {post.likeCount}</div>
            <div>VIEWS: {post.views}</div>
          </div>
          <div id="title">
            <strong>TITLE:</strong> {post.title}
          </div>
          <div id="cont">
            <div>
              <strong>CONTENT</strong>
            </div>
            <div>{post.content}</div>
          </div>
        </div>
      </div>
      <Sidebar />
    </MainContainer>
  );
}

export default QuestionDetail;

